INSERT INTO `txt_fra` VALUES ('Laser Epson 01', '              <ol>\r\n                <li>Retirer les vis qui maintiennent le couvercle.</li>\r\n                <li>Enlever le couvercle et vous trouverez le bouchon de l%B4orifice de remplissage, enlevez-le.</li>\r\n                <li>Secouer le sachet de toner.</li>\r\n                <li>Couper un des coins du sachet environ 5 mm. Introduire le tube fourni dans le kit par l%B4orifice et scotchez-le.</li>\r\n                <li>Pressionner sur le sachet pour faire rentrer le toner et secouez la cartouche pour le répartir à l%B4intérieur. </li>\r\n                <li>Chacun des sachets est égale à une recharge. </li>\r\n                <li>Refermer l%B4orifice de remplissage et remettez le couvercle. </li>\r\n                <li>Après une demi-heure d%B4utilisation, vérifiez qu%B4il n%B4y est pas de fuite.<br>\r\n</ol>');
